use super::solution::Solution;

macro_rules! max_length_tests {
    (
        $(
            $name:ident : $input:expr => $expected:expr
        ),* $(,)?
    ) => {
        $(
            #[test]
            fn $name() {
                let (nums, k) = $input;
                let expected = $expected;
                assert_eq!(Solution::maximum_length(nums, k), expected);
            }
        )*
    };
}

